import * as React from "react";
import { render } from "react-dom";


export default function Timer2() {
  const [counter, setCounter] = React.useState(10);
  React.useEffect(() => {
    counter > 0 && setTimeout(() => setCounter(counter - 1), 1000);
  }, [counter]);

  return (
    <div>
      <div>Countdown: {counter}</div>
    </div>
  );
}
