import React, { Component } from "react"
import Square from './Square.js';

export default class Board extends Component{
    constructor(props) {
        super(props);
        this.state = {
            isWhite: true
        }
    }
    onClick(i)
    {
        this.setState({
            isWhite:false
        })
    }
    renderSquare(i)
    {
       return <Square
       onClick={()=>this.onClick(i)}
       value={this.props.value}
       className={this.state.isWhite?'black-card':'white-card'}
       />
    }
    render()
    {
        return(
            <div>
                <div className="board-row">
                   {this.renderSquare(0)}
                   {this.renderSquare(1)}
                   {this.renderSquare(2)}
                </div>
                <div className="board-row">
                   {this.renderSquare(3)}
                   {this.renderSquare(4)}
                   {this.renderSquare(5)}
                </div>
                <div className="board-row">
                   {this.renderSquare(6)}
                   {this.renderSquare(7)}
                   {this.renderSquare(8)}
                </div>
            </div>
        )
    }
}

