import Board from './Board.js';
import Timer2 from './Timer2.js';

export default class Game1 extends Component {
  constructor(props) {
    super(props);
    this.state = {
        start: 2,
    };
}
   /* handleClick(i)
    {
        this.setState({
            isWhite:false
        })
    }*/
  
startButton()
{
  if (this.state.start == 2){
  this.setState({
    start : 1
  });
}
if (this.state.start == 1){
  this.setState({
    start : 0
  });
}
}
    render(){
      let buttonStart = (this.state.start ==0 )?
       <Timer2/> : null
       let buttonStart2 = (this.state.start ==0 )?
       <Board/> : null
  return (

    <div>
           <h4>Paspaudus mygtuką start per duotą laiką įsiminkite juodus 
             kvadratėlius. <br/>Kai laikmatis pasibaigs paspauskite ant kvadratėlių
             kurie prieš tai buvo juodi.
           </h4>
           <button onClick={this.startButton()}>Start</button>
           {buttonStart}
           {buttonStart2}
    </div>
  );
    }
}
