import React, { Component } from "react"
import Square from './Square.js';

export default class Board extends Component{
   
    /*Square(i, classNameOfSquare)
    {
      return  <button onClick={this.onClick(i, classNameOfSquare)} className={classNameOfSquare}>            
       </button>
    }*/
   /* onClick(i, classNameOfSquare)
    {
        if (classNameOfSquare==='white-card')
        {
            return  <button onClick={this.onClick(i, 'black-card')} className='black-card'>            
            </button>
        }
*/
       /* this.setState({
            isWhite:false
        })*/
       /* console.log(156);
       if (color)
         return false;
       else
          return true;*/
 /*   }*/
    renderSquare(i, color)
    {
       /*return <Square
       onClick={()=>this.onClick(i, color)}
       value={this.props.value}
       className={this.props.state?'black-card':'white-card'}
       />*/
     /*  if (color)
       {
           return this.Square(i, 'white-card')
       }
       return this.Square(i, 'black-card');*/
       return <Square color='black-card'
       />
    }
    render()
    {
        return(
            <div>
                <div className="board-row">
                   {this.renderSquare(0, true)}
                   {this.renderSquare(1, true)}
                   {this.renderSquare(2, false)}
                </div>
                <div className="board-row">
                   {this.renderSquare(3, true)}
                   {this.renderSquare(4, true)}
                   {this.renderSquare(5, true)}
                </div>
                <div className="board-row">
                   {this.renderSquare(6, false)}
                   {this.renderSquare(7, true)}
                   {this.renderSquare(8, false)}
                </div>
            </div>
        )
    }
}

