import React, {Component} from 'react'

export default class Square extends Component{
    constructor(props) {
        super(props);

    }
    
    onClick(i)
    {
      if (props.color==='white-card')
        this.button.className='black-card';
      else{
          this.button.className='white-card';
      }
    }
    render(){
    return (
        <button className= {props.color}
         onClick={this.onClick()}>
        </button>
        /*<button></button>*/
    )
    }
}

