import React, {Component} from 'react'
export default class Square extends Component {
    constructor(props) {
        super(props);
        this.changeColor= this.changeColor.bind(this);
        this.state = {
            color: this.props.color,
            index: this.props.index,
            exactColor:this.props.exactColor,
        };
    }
    changeColor() {
     const currentState = this.props.color;
        this.setState({ color: !currentState });
    };


    render() {
       function wrongColor()
       {
           if (this.exactColor==this.props.color){
               return <p>Pasirinktas blogas kvadratas</p>
           }
       }

        return (
            <div>
            <button
                className={this.state.color ? 'white-card' : 'black-card'}
                onClick={this.changeColor}
            >
            </button>
            {this.wrongColor}

            </div>

        )
  }
}
